#include <iostream>
#include <fstream>
#include <string>

int main() {
    std::string filename, word;
    int count = 0;

    // Get the filename and word from the user
    std::cout << "Enter the filename: ";
    std::cin >> filename;
    std::cout << "Enter the word to count: ";
    std::cin >> word;

    // Open the file
    std::ifstream file(filename);

    // Check if the file was opened successfully
    if (!file.is_open()) {
        std::cout << "Error opening file!" << std::endl;
        return 1;
    }

    // Read the file line by line
    std::string line;
    while (std::getline(file, line)) {
        // Count the occurrences of the word in the line
        size_t pos = 0;
        while ((pos = line.find(word, pos))!= std::string::npos) {
            count++;
            pos += word.length();
        }
    }

    // Close the file
    file.close();

    // Print the count
    std::cout << "The word \"" << word << "\" appears " << count << " times in the file." << std::endl;

    return 0;
}
