```cpp
#include <iostream>
#include <fstream>
#include <string>

int main() {
    std::string filename, searchWord;
    int count = 0;

    // Get input for the file name and the word to search
    std::cout << "Enter the file name: ";
    std::cin >> filename;
    std::cout << "Enter the word to search: ";
    std::cin >> searchWord;

    // Open the file
    std::ifstream file(filename);

    // Check if the file is open, if not display an error message and exit
    if (!file.is_open()) {
        std::cerr << "Error opening the file." << std::endl;
        return 1;
    }

    // Read the file line by line
    std::string line;
    while (std::getline(file, line)) {
        // Count the number of occurrences of the word in the current line
        size_t pos = 0;
        while ((pos = line.find(searchWord, pos)) != std::string::npos) {
            ++count;
            pos += searchWord.length();
        }
    }

    // Close the file
    file.close();

    // Display the result
    std::cout << "The word '" << searchWord << "' occurs " << count << " times in the file." << std::endl;

    return 0;
}
