#include <iostream>
#include <fstream>
#include <string>
#include <vector>

int main() {
    std::ifstream file("example.txt"); // Replace with your file path
    std::string line;
    std::string word_to_count = "word_to_count"; // Replace with the word to count
    std::vector<std::string> lines;
    int count = 0;

    // Read the file line by line
    while (std::getline(file, line)) {
        lines.push_back(line);
    }

    // Count the occurrences of the word
    for (const auto& line : lines) {
        if (line.find(word_to_count) != std::string::npos) {
            ++count;
        }
    }

    std::cout << "The word '" << word_to_count << "' occurs " << count << " times in the file." << std::endl;

    return 0;
}
```

