    std::vector<int> fibonacci_numbers(n + 1);
    fibonacci_numbers[0] = 0;
    fibonacci_numbers[1] = 1;

    for (int i = 2; i <= n; ++i) {
        fibonacci_numbers[i] = fibonacci_numbers[i - 1] + fibonacci_numbers[i - 2];
